import React, { useEffect, useState } from 'react'
import axios from 'axios'
import { Link } from 'react-router-dom'

const TravelBook = () => {

    const [travelBook, setTravelBook] = useState([])
    const [id, setId] = useState('')
    
    const fetchData = async () => {
        const { data } = await axios.get('http://localhost:7000/api/travel')
        setTravelBook(data.travels)
    }


    const DeleteHendler = async (e) => {
        e.preventDefault()
        await axios.delete(`http://localhost:7000/api/travel/${id}`)
        fetchData()

    }
    
    useEffect(() => {fetchData()}, [])


    
    
  return (
    <div>
        {travelBook.map(tb => (
            <div key={tb._id} className="card mb-3 mt-3">
            <img src={tb.image} className="card-img-top" alt={tb.title}/>
            <div className="card-body">
                <h5 className="card-title">{tb.title}</h5>
                <p className="card-text">{tb.descr}</p>
                <div className='d-flex justify-content-start'>
                    <Link className='btn btn-primary' to={`/update/${tb._id}`}>Update</Link>
                    <form onSubmit={DeleteHendler}>
                        <Link className='btn btn-danger mx-2' onClick='('>Delete</Link>
                    </form>
                </div>
            </div>
        </div>
        ))}
        
    </div>
  )
}

export default TravelBook
