import axios from 'axios'
import React, { useEffect, useState } from 'react'
import { useNavigate, useParams } from 'react-router-dom'



const UpdateBook = () => {

    const [title, setTitle] = useState('')
    const [descr, setDescr] = useState('')
    const [image, setImage] = useState('')

    const { id } = useParams()
    const navigate = useNavigate()

    const fetchData = async () => {
        const { data } = await axios.get(`http://localhost:7000/api/travel/${id}`)
        setTitle(data.travel.title)
        setDescr(data.travel.descr)
        setImage(data.travel.image)
    }
    

    const updateHendler = async 
    useEffect(() => {
        fetchData()
    }, []);

  return (
    <div>
        <div>
    <form onSubmit={updateHendler}>
        <div className="mb-3">
            <label htmlFor="title" className="form-label">Title</label>
            <input type="text" className="form-control" id="title" name='title' value={title} onChange={e => setTitle(e.target.value)}/>
        </div>
        <div className="mb-3">
            <label htmlFor="descr" className="form-label">Description</label>
            <input type="text" className="form-control" id="descr" name='descr' value={descr} onChange={e => setDescr(e.target.value)}/>
        </div>
        <div className="mb-3">
            <label htmlFor ="image" className="form-label">Image URL</label>
            <input type="text" className="form-control" id="image" name='image' value={image} onChange={e => setImage(e.target.value)}/>
        </div>
        <button type="submit" className="btn btn-primary">Submit</button>
    </form>
</div>
</div>
  )
}

export default UpdateBook
